def Part1():
    floor = 0
    for i in puzzleInput:
        if i == ")":
            floor -= 1
        else:
            floor += 1
    return floor

def Part2():
    floor = 0
    for i in range(len(puzzleInput)):
        if puzzleInput[i] == ")":
            floor -= 1
        else:
            floor += 1
        if floor == -1:
            return i+1  

